public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		int b=10;
		if(a>b)
			System.out.println("a is greater than b");
		else if(b>a)
			System.out.println("b is greater than a");
		else
			System.out.println("a and b are equal");
	}

}
